import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { SkillStateContext, SkillDispatchContext } from 'context/skill'
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import InfoCard from './InfoCard'
import Button from 'react-bootstrap/Button';

const Title = styled.h5`
    color: #262b33;
    margin: 10px 0px;
`

const Skill = (skill) => (
    <div>
        <span>{skill}</span>

    </div>
)

const Skills = () => {
    const state = useContext(SkillStateContext)
    const dipatch = useContext(SkillDispatchContext)
    const [input, setInput] = useState('')
    const onClick = () => {
        dipatch({
            type: 'add',
            payload: input
        })
        setInput('')
    };
    console.log(state)

    return (
        <InfoCard title={'Kompetens'}>
            {state.map((skill)=>
                <div>
                    <span>{skill}</span>
                </div>
            )}
            <InputGroup>
                <Form.Control
                    placeholder="Recipient's username"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    onChange={(event) => setInput(event.target.value)}
                />
                <InputGroup.Append>
                    <Button onClick={onClick} variant="outline-secondary">Button</Button>
                </InputGroup.Append>
            </InputGroup>
        </InfoCard>
    )
}



export default Skills
