import React, { useState, useContext, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import InfoCard from './InfoCard';
import { ExperienceStateContext, ExperienceDispatchContext } from 'context/experience'
import styled from 'styled-components';
import { Trash, ArrowUp, ArrowDown, BoxArrowUpRight, BoxArrowInDownLeft  } from 'react-bootstrap-icons';
import { FormLabel } from './Form';
import Collapse from 'react-bootstrap/Collapse';

const TitleWrapper = styled.div`
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: space-between;
    align-items: center;
`

const CardWrapper = styled.div`
    margin-bottom: 18px;
    border:1px solid #ced4da;
    border-radius: 5px;
    padding: 0px 18px;
`

const Title = styled.span`
    font-weight: bold;
    color: #B2B8BE;
`

const DeleteIcon = styled(Trash)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const ArrowUpIcon = styled(ArrowUp)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const ArrowDownIcon = styled(ArrowDown)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const Experience = ({employer, role, index}) => {
    let title = employer + ( role ? ' - ' + role : '')
    let typingTimer = null;
    const [ displayInfo, setDisplayInfo ] = useState(true)
    const dipatch = useContext(ExperienceDispatchContext)
    const state = useContext(ExperienceStateContext)

    const onChange = (event) => {
        dipatch({
            type: 'update',
            index: index,
            fieldName: event.target.id,
            payload: event.currentTarget.value,
        })
    }

    const onDelete = (event) => {
        dipatch({
            type: 'delete',
            index: index,
        })
    }

    useEffect(() => {
        return function cleanup() {
            clearTimeout(typingTimer);
        };
    });

    const onChangeTimeout = (event) => {
        const val = event.target.value;
        const id = event.target.id;
        clearTimeout(typingTimer);
        typingTimer = setTimeout(() => {
            if (val) {
                dipatch({
                    type: 'update',
                    index: index,
                    fieldName: id,
                    payload: val,
                })
            }
        }, 3000);
    }

    return (
        <CardWrapper>
            <TitleWrapper onClick={() =>setDisplayInfo(!displayInfo)}>
                <Title>{title || ''}</Title>
                <div>
                    {displayInfo ? 
                        <ArrowUpIcon fill={'#B2B8BE'}/> :
                        <ArrowDownIcon  fill={'#B2B8BE'}/>
                    }
                    <DeleteIcon onClick={onDelete}  fill={'#B2B8BE'}/>
                </div>
            </TitleWrapper>
            <Collapse in={displayInfo}>
                <div>
                    <Form.Row>
                        <Form.Group as={Col} controlId="employer">
                            <FormLabel>Arbetsgivare</FormLabel>
                            <Form.Control onBlur={onChange} />
                        </Form.Group>

                        <Form.Group as={Col} controlId="workTitle">
                            <FormLabel>Titel</FormLabel>
                            <Form.Control type="text" onBlur={onChange}/>
                        </Form.Group>

                    </Form.Row>

                    <Form.Row>
                        <Form.Group as={Col} controlId="startDate">
                            <FormLabel>Start</FormLabel>
                            <Form.Control onBlur={onChange} type="date"/>
                        </Form.Group>

                        <Form.Group as={Col} controlId="endDate">
                            <FormLabel>Slut</FormLabel>
                            <Form.Control onBlur={onChange} type="date"/>
                        </Form.Group>
                    </Form.Row>

                    <Form.Group controlId="description">
                        <FormLabel>Beskrivning</FormLabel>
                        <Form.Control onChange={onChangeTimeout} as="textarea" rows="4"/>
                    </Form.Group>
                </div>
            </Collapse>
        </CardWrapper>
    )
}



export default Experience
