import React from 'react';
import { useImmerReducer } from 'use-immer';

function cvReducer(draft, action) {
  switch (action.type) {
    case 'field': {
      draft[action.fieldName] = action.payload;
      return;
    }
    default:
      return;
  }
}


const initialState = {
  firstName: '',
  lastName: '',
  email: '',
  phone: ''
};

const UserStateContext = React.createContext();
const UserDispatchContext = React.createContext();

const UserContext = ({children}) => {
  const [state, dispatch] = useImmerReducer(cvReducer, initialState);

  return (
    <UserStateContext.Provider value={dispatch}>
      <UserDispatchContext.Provider value={state}>
        {children}
      </UserDispatchContext.Provider>
    </UserStateContext.Provider>
  );
}

export { UserContext, UserDispatchContext, UserStateContext }
