import React, { useContext } from 'react';
import Button from 'react-bootstrap/Button';
import Experience from './Experience';
import { PlusSquare } from 'react-bootstrap-icons';
import styled from 'styled-components';
import { ExperienceStateContext, ExperienceDispatchContext } from 'context/experience'


const ButtonText = styled.span`
    margin-left: 10px;
`

const SubmitButton = styled(Button)`
    display:flex;
    align-items: center;
    padding: 10px;
`

const Experiences = () => {
    const state = useContext(ExperienceStateContext)
    console.log(state)
    const dipatch = useContext(ExperienceDispatchContext)
    const handleSubmit = (event) => {
        dipatch({
            type: 'ADD_EXPERIENCE',
        })
    };

    return (
        <React.Fragment>
            <h4>Tidigare erfarenheter</h4>
            {state.map((experience, index)=>
                <Experience
                    employer={experience.employer}
                    role={experience.workTitle}
                    index={index}
                    key={experience.employer + index}
                />
            )}
            <SubmitButton variant="primary" type="submit" onClick={handleSubmit}>
                <PlusSquare/>
                <ButtonText>LÃ¤gg till erfarenhet</ButtonText>
            </SubmitButton>
        </React.Fragment>
    )
}



export default Experiences
