import React, { useContext } from 'react';
import PropTypes from 'prop-types';

import {
    PDFViewer,
    Document,
    Page,
    StyleSheet,
    View,
    Font
} from '@react-pdf/renderer';
import Header from './Header';
import Education from './Education';
import Experience from './Experience';
import Skills from './Skills';
import { StateContext } from '../../context/user';

Font.register({
    family: 'Oswald',
    src: 'https://fonts.gstatic.com/s/oswald/v13/Y_TKV6o8WovbUd3m_X9aAA.ttf'
  });


const styles = StyleSheet.create({
    document: {
        width: '100%',
        height: '100vh',
        fontFamily: 'Oswald'
    },
    page: {
        padding: 30,
        width: '100%',
        height: '100vh'
    },
    container: {
        flex: 1,
        flexDirection: 'row',
    },
    image: {
        marginBottom: 10,
    },
    leftColumn: {
        flexDirection: 'column',
        width: 170,
        paddingTop: 30,
        paddingRight: 15,
    },
    rightColumn: {
        flexDirection: 'column',
        width: '100%',
        paddingTop: 30,
        paddingRight: 15,
    },
    footer: {
        fontSize: 12,
        textAlign: 'center',
        marginTop: 25,
        paddingTop: 10,
        borderWidth: 3,
        borderColor: 'gray',
        borderStyle: 'dashed',
        '@media orientation: landscape': {
        marginTop: 10,
        },
    },
});


const Resume = ({name, email, education, skills, experience}) => {
    const state = useContext(StateContext)
    return (
        <PDFViewer style={styles.document}>
            <Document>
                <Page size="A4" style={styles.page}>
                    <Header />
                    <View style={styles.container}>
                        <View style={styles.leftColumn}>
                            <Education education={education}/>
                            <Skills skills={skills}/>
                        </View>
                        <View style={styles.rightColumn}>
                            <Experience experience={experience}/>
                        </View>
                    </View>
                </Page>
            </Document>
        </PDFViewer>
    )
}

Resume.propTypes = {
    name: PropTypes.string,
    email: PropTypes.string,
    education: PropTypes.arrayOf(PropTypes.object),
    skills: PropTypes.arrayOf(PropTypes.object),
    experience: PropTypes.arrayOf(PropTypes.object)
};

Resume.defaultProps = {
    name: 'Karl Jansson',
    email: 'karljansson@gmail.com',
    education: [{
        school: 'Solbergagymnasiet',
        degree: 'Natur',
        start: 'Q1 2000',
        end: 'Q2 2005'
    },
    {
        school: 'Lunds tekniska unversitet',
        degree: 'Master Datateknik',
        start: 'Q1 2005',
        end: 'Q2 2011'
    }],
    skills: [{
        title: 'Programmering',
        skills: ['Javscript', 'Python']
    },
    {
        title: 'Språk',
        skills: ['Engelska', 'Svenska']
    }],
    experience: [{
        position: 'Kundtjänst',
        company: 'Aspiranterna',
        date: 'Q1 2000 - Q2 2005',
        details: [
            'Problemlösning och kundkontakt var två ledord för min roll på Aspiranterna.', 
            'Jag lärde mig det interna butikssystemet utantill och fick en bred förståelse för företagets produkter.'
        ]
    },
    {
        position: 'HR assistent',
        company: 'Spotify',
        date: 'Q3 2011 - Q2 2015',
        details: [
            'Ett administrativt jobb som utvecklades till att jag fick ansvar för både lönesättning och personalsamtal.', 
            'Jag lärde mig hur man ska handskas med personer och att det alltid finns fler infallsvinklar som måste analyseras för att lösa ett problem.'
        ]
    }]
};

export default Resume
