import Button from 'react-bootstrap/Button';
import Experience from './Experience';
import { PlusSquare } from 'react-bootstrap-icons';
import styled from 'styled-components';
import { ExperienceStateContext, ExperienceDispatchContext } from 'context/experience'


const ButtonText = styled.span`
    margin-left: 10px;
`

const SubmitButton = styled(Button)`
    display:flex;
    align-items: center;
    justify-content: center;
    padding: 10px;
    width: 100%;
`

const Title = styled.h5`
    color: #262b33;
    margin: 10px 0px;
`


const Experiences = () => {
    const state = useContext(ExperienceStateContext)
    const dipatch = useContext(ExperienceDispatchContext)
    const handleSubmit = (event) => {
        dipatch({
            type: 'ADD_EXPERIENCE',
        })
    };

    return (
        <React.Fragment>
            <Title>Tidigare erfarenheter</Title>
