import React, {useState} from 'react';
import styled from 'styled-components';
import UserInfo from './UserInfo';
import Summary from './Summary';
import Experiences from './Experiences';
import Skills from './Skills'
import Educations from './Educations'
import InfoCard from './InfoCard'

const Wrapper = styled.div`
  display: block;
  padding 24px;
`

export default () => {
  const [focusCard, setFocusCard] = useState('UserInfo')
  return (
    <Wrapper>
      <InfoCard title={'Personuppgifter'} display={focusCard === 'UserInfo'} setDisplay={() => setFocusCard(focusCard === 'UserInfo' ? 'none' : 'UserInfo')} >
        <UserInfo/>
      </InfoCard>
      <InfoCard title={'Sammanfattning av erfarenheter'} display={focusCard === 'Summary'} setDisplay={() => setFocusCard(focusCard === 'Summary' ? 'none' : 'Summary')}>
        <Summary/>
      </InfoCard>
      <InfoCard title={"Tidigare erfarenheter"} display={focusCard === 'Experiences'} setDisplay={() => setFocusCard(focusCard === 'Experiences' ? 'none' : 'Experiences')}>
        <Experiences/>
      </InfoCard>
      <InfoCard title={"Utbildning"} display={focusCard === 'Educations'} setDisplay={() => setFocusCard(focusCard === 'Educations' ? 'none' : 'Educations')}>
        <Educations/>
      </InfoCard>
      <InfoCard title={'Kompetens'} display={focusCard === 'Skills'} setDisplay={() => setFocusCard(focusCard === 'Skills' ? 'none' : 'Skills')}>
        <Skills/>
      </InfoCard>
    </Wrapper>
  )
}
