import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { SkillStateContext, SkillDispatchContext } from 'context/skill'
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import InfoCard from './InfoCard'
import Button from 'react-bootstrap/Button';

const Title = styled.h5`
    color: #262b33;
    margin: 10px 0px;
`

const Skill = (skill) => (
    <React.Fragment>
        <span>{skill}</span>

    </React.Fragment>
)

const Skills = () => {
    const state = useContext(SkillStateContext)
    const dipatch = useContext(SkillDispatchContext)
    const [input, setInput] = useState('')
    const onClick = () => {
        console.log(input)
        dipatch({
            type: 'add',
            payload: input
        })
        setInput('')
    };

    return (
        <InfoCard title={'Kompetens'}>
            {state.map((skill, index)=>
                <Skill
                    skill={skill}
                />
            )}
            <InputGroup>
                <Form.Control
                    placeholder="Recipient's username"
                    aria-label="Recipient's username"
                    aria-describedby="basic-addon2"
                    onChange={(event) => setInput(event.target.value)}
                />
                <InputGroup.Append>
                    <Button onClick={onClick} variant="outline-secondary">Button</Button>
                </InputGroup.Append>
            </InputGroup>
        </InfoCard>
    )
}



export default Skills
