import React, { useState } from 'react';
import Template from './Templates/Simple';
import CvForm from './CvForm/CvForm';
import styled from 'styled-components';
import { UserContext } from './context/user'

const Half = styled.div`
    width: ${props => props.display ? props.display : "0%" };
    display: ${props => props.display ? "block" : "none"};
`

const Wrapper = styled.div`
    display: flex;
    flex-direct: row;
`

const displayMode = {
    SplitScreen: {
        formWidth: "50%",
        pdfWidth: "50%",
        displayForm: true,
        displayPdf: true
    },
    PDFView: {
        formWidth: "0%",
        pdfWidth: "100%",
        displayForm: false,
        displayPdf: true
    },
    FormView: {
        formWidth: "100%",
        pdfWidth: "0%",
        displayForm: true,
        displayPdf: false,
    },

}

const App = () => {
    //0 = split
    //1 = pdf fullscreen
    //2 = form fullscreen
    const [displayMode, setDisplayMode] = useState(0)

    return (
        <Wrapper>
            <UserContext>
                <Half
                    display={false}
                >
                    <CvForm/>
                </Half>
                <Half
                    display="50%"
                >
                    <Template/>
                </Half>
            </UserContext>
        </Wrapper>
    )
  }

export default App;
