import React, { useState } from 'react';
import Template from './Templates/Simple';
import CvForm from './CvForm/CvForm';
import styled from 'styled-components';
import { UserContext } from 'context/user'
import { ExperienceContext } from 'context/user'

const Half = styled.div`
    width: ${props => props.width };
    display: ${props => props.display};
`

const Wrapper = styled.div`
    display: flex;
    flex-direct: row;
`



const App = () => {
    const displayModes = {
        SplitScreen: {
            formWidth: "50%",
            pdfWidth: "50%",
            formDisplay: "block",
            pdfDisplay: "block"
        },
        PDFView: {
            formWidth: "0%",
            pdfWidth: "100%",
            formDisplay: "none",
            pdfDisplay: "block"
        },
        FormView: {
            formWidth: "100%",
            pdfWidth: "0%",
            formDisplay: "block",
            pdfDisplay: "none",
        },

    }

    const [displayMode, setDisplayMode] = useState(displayModes.SplitScreen)

    return (
        <Wrapper>
            <UserContext>
                <Half
                    width={displayMode.formWidth}
                    display={displayMode.formDisplay}
                >
                    <CvForm/>
                </Half>
                <Half
                    width={displayMode.pdfWidth}
                    display={displayMode.pdfDisplay}
                >
                    <Template/>
                </Half>
            </UserContext>
        </Wrapper>
    )
  }

export default App;
