import React from 'react';

import Title from '../Components/Title';
import List, { Item } from './List';
import { Text, View, StyleSheet } from '@react-pdf/renderer';
import PropTypes from 'prop-types';

const styles = StyleSheet.create({
  entryContainer: {
    marginBottom: 10,
  },
  date: {
    fontSize: 9,
  },
  detailContainer: {
    flexDirection: 'row',
    fontSize: 10,
  },
  headerContainer: {
    flexDirection: 'row',
    marginBottom: 10,
  },
  leftColumn: {
    flexDirection: 'column',
  },
  rightColumn: {
    flexDirection: 'column',
    flexGrow: 1,
    alignItems: 'flex-end',
    justifySelf: 'flex-end',
    fontSize: 9
  },
  title: {
    fontSize: 11,
    color: 'black',
    textDecoration: 'none',
  },
});

const ExperienceEntry = ({ company, detail, position, date }) => {
  const title = `${company} | ${position}`;
  return (
    <View style={styles.entryContainer}>
      <View style={styles.headerContainer}>
        <View style={styles.leftColumn}>
          <Text style={styles.title}>{title}</Text>
        </View>
        <View style={styles.rightColumn}>
          <Text style={styles.date}>{date}</Text>
        </View>
      </View>

      <Text style={styles.detailContainer}>
        {detail}
      </Text>
    </View>
  );
};

const Experience = ({experience}) => (
  <View>
    <Title>Arbetshistoria</Title>
    {experience.map(({ company, date, detail, position }) => (
      <ExperienceEntry
        company={company}
        date={date}
        detail={detail}
        key={company + position}
        position={position}
      />
    ))}
  </View>
);

Experience.propTypes = {
  experience: PropTypes.arrayOf(PropTypes.object)
};

Experience.defaultProps = {
  experience: [{
      position: 'Kundtjänst',
      company: 'Aspiranterna',
      date: 'Jan 2000 - Okt 2005',
      detail: 'Problemlösning och kundkontakt var två ledord för min roll på Aspiranterna. Jag lärde mig det interna butikssystemet utantill och fick en bred förståelse för företagets produkter.'
  },
  {
      position: 'HR assistent',
      company: 'Spotify',
      date: 'Feb 2011 - Dec 2015',
      detail: 'Ett administrativt jobb som utvecklades till att jag fick ansvar för både lönesättning och personalsamtal. Jag lärde mig hur man ska handskas med personer och att det alltid finns fler infallsvinklar som måste analyseras för att lösa ett problem.'
  }]
};

export default Experience;
