import React, { useContext } from 'react';
import styled from 'styled-components';
import { SkillStateContext, SkillDispatchContext } from 'context/skill'
import { Trash } from 'react-bootstrap-icons';
import InfoCard from './InfoCard'

import Button from './Button'

const Title = styled.h5`
    color: #262b33;
    margin: 10px 0px;
`

const Skill = (skill) => {
    <React.Fragment>
        <span>{skill}</span>

    </React.Fragment>
}

const Skills = () => {
    const state = useContext(SkillStateContext)
    const dipatch = useContext(SkillDispatchContext)
    const handleSubmit = (event) => {
        dipatch({
            type: 'add',
            payload: 'event.target.value'
        })
    };

    return (
        <InfoCard>
            <Title>Kompetens</Title>
            {state.map((skill, index)=>
                <Skill
                    skill={skill}
                />
            )}
            <Button
                onClick={handleSubmit}
                label={'Lägg till erfarenhet'}
            />
        </InfoCard>
    )
}



export default Skills
