import React, { useState, useContext, useEffect } from 'react';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import InfoCard from './InfoCard';
import { ExperienceDispatchContext } from 'context/experience'
import styled from 'styled-components';
import { Trash, ArrowUp, BoxArrowUpRight, BoxArrowInDownLeft  } from 'react-bootstrap-icons';

const TitleWrapper = styled.div`
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
`

const Title = styled.span`
    font-weight: bold;
`

const DeleteIcon = styled(Trash)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const HideWrapper = styled.div`
    display: ${props => props.display ? "block" : "none"};
`

const ArrowUpIcon = styled(BoxArrowUpRight)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const ArrowDownIcon = styled(BoxArrowInDownLeft)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const Experience = ({employer, role, index}) => {
    let title = role + ( employer ? ' - ' + employer : '')
    let typingTimer = null;
    const [ displayInfo, setDisplayInfo ] = useState(true)
    const dipatch = useContext(ExperienceDispatchContext)

    const onChange = (event) => {
        dipatch({
            type: 'update',
            index: index,
            fieldName: event.target.id,
            payload: event.currentTarget.value,
        })
    }

    const onDelete = (event) => {
        dipatch({
            type: 'delete',
            index: index,
        })
    }

    useEffect(() => {
        return function cleanup() {
            clearTimeout(typingTimer);
        };
    });

    const onChangeTimeout = (event) => {
        const val = event.target.value;
        const id = event.target.id;
        clearTimeout(typingTimer);
        typingTimer = setTimeout(() => {
            if (val) {
                dipatch({
                    type: 'update',
                    index: index,
                    fieldName: id,
                    payload: val,
                })
            }
        }, 3000);
    }

    return (
        <InfoCard>
            <TitleWrapper>
                <Title>{title || '---'}</Title>
                <div>
                    <DeleteIcon onClick={onDelete}/>
                    {displayInfo ? 
                        <ArrowDownIcon onClick={() => setDisplayInfo(false)}/> :
                        <ArrowUpIcon onClick={() => setDisplayInfo(true)}/>
                    }

                </div>
            </TitleWrapper>
            <HideWrapper display={true}>
                <Form.Row>
                    <Form.Group as={Col} controlId="workTitle">
                        <Form.Label>Titel</Form.Label>
                        <Form.Control type="text" onChange={onChange}/>
                    </Form.Group>

                    <Form.Group as={Col} controlId="employer">
                        <Form.Label>Arbetsgivare</Form.Label>
                        <Form.Control onChange={onChange} />
                    </Form.Group>
                </Form.Row>

                <Form.Row>
                    <Form.Group as={Col} controlId="startDate">
                        <Form.Label>Start</Form.Label>
                        <Form.Control onChange={onChange} type="date"/>
                    </Form.Group>

                    <Form.Group as={Col} controlId="endDate">
                        <Form.Label>Slut</Form.Label>
                        <Form.Control onChange={onChange} type="date"/>
                    </Form.Group>
                </Form.Row>

                <Form.Group controlId="description">
                    <Form.Label>Beskrivning</Form.Label>
                    <Form.Control onChange={onChangeTimeout} as="textarea" rows="4"/>
                </Form.Group>
            </HideWrapper>
        </InfoCard>
    )
}



export default Experience
