import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { SkillStateContext, SkillDispatchContext } from 'context/skill'
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Button from 'react-bootstrap/Button';
import { Backspace } from 'react-bootstrap-icons';


const SkillWrapper = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    margin-top: 10px;
`

const DeleteIcon = styled(Backspace)`
    width: 24px;
    height: 24px;
`

const SkillCard = styled.div`
    background-color: #557A95;
    padding: 5px 10px;
    margin: 5px 5px 5px 0px;
    color: white;
    border-radius: 10px;
`

const Skill = ({skill}) => (
    <SkillCard>
        <span>{skill}</span>
        <Backspace fill={'white'}/>
    </SkillCard>
)

const Skills = () => {
    const state = useContext(SkillStateContext)
    const dipatch = useContext(SkillDispatchContext)
    const [input, setInput] = useState('')

    const onClick = () => {
        dipatch({
            type: 'add',
            payload: input
        })
        setInput('')
    };

    const handleKeyDown = (event) => {
        if (event.key === 'Enter') {
            dipatch({
                type: 'add',
                payload: input
            })
            setInput('')
        }
      }

    console.log(state)

    return (
        <React.Fragment>
            <InputGroup onKeyDown={handleKeyDown}>
                <Form.Control
                    aria-describedby="basic-addon2"
                    onChange={(event) => setInput(event.target.value)}
                    value={input}
                    />
                <InputGroup.Append>
                    <Button onClick={onClick} variant="outline-secondary">LÃ¤gg till kompetens</Button>
                </InputGroup.Append>
            </InputGroup>
            <SkillWrapper>
                {state.map((value, index)=>
                    <Skill
                        key={value}
                        skill={value}
                    />
                )}
            </SkillWrapper>
        </React.Fragment>
    )
}



export default Skills
