import React from 'react';
import Card from 'react-bootstrap/Card';
import styled from 'styled-components';


const CardWrapper = styled(Card)`
    margin-bottom: 12px;
`

const InfoCard = ({title, children}) => (
    <React.Fragment>
        {title && <h4>{title}</h4>}
        <CardWrapper>
            <Card.Body>
                {children}
            </Card.Body>
        </CardWrapper>
    </React.Fragment>
)

export default InfoCard
