import React, { useState } from 'react';
import Card from 'react-bootstrap/Card';
import styled from 'styled-components';
import { BoxArrowUpRight, BoxArrowInDownLeft  } from 'react-bootstrap-icons';


const CardWrapper = styled(Card)`
    margin-bottom: 12px;
    flex-grow: 1;
`
const Title = styled.h4`
    margin-top: 10px;
    margin-left: 20px;
    margin-bottom: 0px;
`

const ArrowUpIcon = styled(BoxArrowUpRight)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const ArrowDownIcon = styled(BoxArrowInDownLeft)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const TitleWrapper = styled.div`
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
`

const HideWrapper = styled.div`
    display: ${props => props.displayInfo ? "block" : "none"};
`

const InfoCard = ({title, children}) => {
    const [ displayInfo, setDisplayInfo ] = useState(true)

    return (
        <React.Fragment>
            <CardWrapper>
                <TitleWrapper onClick={() =>setDisplayInfo(!displayInfo)}>
                    {title && <Title>{title}</Title>}
                    {displayInfo ? 
                        <ArrowDownIcon/> :
                        <ArrowUpIcon/>
                    }
                </TitleWrapper>
                <HideWrapper displayInfo={displayInfo}>
                    <Card.Body>
                    {children}
                    </Card.Body>
                </HideWrapper>
            </CardWrapper>
        </React.Fragment>
    )
}


export default InfoCard
