import React, { useState } from 'react';
import styled from 'styled-components';
import loginImg from 'assets/jpg/signin-image.jpg'
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Button from 'react-bootstrap/Button';

const ButtonText = styled.span`
    margin-left: 10px;
`

const SubmitButton = styled(Button)`
    display:flex;
    align-items: center;
    justify-content: center;
    padding: 10px;
    width: 100%;
`


const LoginForm = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    width: 35%;
    height: 70%;
    background-color: white;
`

const Card = styled.div`
    width: 80%;
    height: 70%;
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direct: row;
    background-color: white;
    border-radius: 10px;
`

const ImgWrapper = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    width: 45%;
    background-color: white;
`

const LoginImg = styled.img`
    width: 75%;
`

const Wrapper = styled.div`
    display: flex;
    background-color: grey;
    align-items: center;
    justify-content: center;
    height: 100vh;
`



export default () => {
    const onClick = (event) =>{
        console.log(event.target)
    }
    return (
        <Wrapper>
            <Card>
                <ImgWrapper>
                    <LoginImg src={loginImg}/>
                </ImgWrapper>
                <LoginForm>
                    <h4>Logga in</h4>
                    <Form>
                        <Form.Group as={Col} controlId="employer">
                            <Form.Control type="text"/>
                        </Form.Group>

                        <Form.Group as={Col} controlId="workTitle">
                            <Form.Control type="password"/>
                        </Form.Group>
                        <SubmitButton variant="light" onClick={onClick}>
                            <ButtonText>Logga in</ButtonText>
                        </SubmitButton>
                    </Form>
                </LoginForm>
            </Card>
        </Wrapper>
    )
}
