import React, { useState } from 'react';
import styled from 'styled-components';
import { ArrowUp, ArrowDown} from 'react-bootstrap-icons';


const CardWrapper = styled.div`
    margin-bottom: 18px;
    border-radius: 5px;
    display: ${props => props.displayInfo ? "block" : "none"};
    border: ${props => props.displayInfo ? "none" : "1px solid #ced4da" };
`

const TitleWrapper = styled.div`
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: space-between;
    align-items: center;
`

const Title = styled.h5`
    color: #262b33;
`

const ArrowUpIcon = styled(ArrowUp)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const ArrowDownIcon = styled(ArrowDown)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const InfoCard = ({title, children}) => {
    const [displayInfo, setDisplayInfo] = useState(true)

    return (
        <React.Fragment>
            <TitleWrapper onClick={() =>setDisplayInfo(!displayInfo)}>
                <Title>{title || ''}</Title>
                <div>
                    {displayInfo ? 
                        <ArrowUpIcon fill={'#B2B8BE'}/> :
                        <ArrowDownIcon  fill={'#B2B8BE'}/>
                    }
                </div>
            </TitleWrapper>
            <CardWrapper displayInfo={displayInfo}>
                {children}
            </CardWrapper>
        </React.Fragment>
    )
}

export default InfoCard
