import React, { useState } from 'react';
import styled from 'styled-components';
import { ArrowUp, ArrowDown} from 'react-bootstrap-icons';
import Collapse from 'react-bootstrap/Collapse';

const TitleWrapper = styled.div`
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: space-between;
    align-items: center;
    border-bottom: ${props => props.displayInfo ? "1px solid white" : "1px solid #ced4da"};
    transition: all .5s ease;
`

const Title = styled.h5`
    color: #262b33;
    margin: 0px;
`

const ArrowUpIcon = styled(ArrowUp)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const ArrowDownIcon = styled(ArrowDown)`
    width: 24px;
    height: 24px;
    margin: 10px;
`

const InfoCard = ({title, children}) => {
    const [displayInfo, setDisplayInfo] = useState(true)

    return (
        <React.Fragment>
            <TitleWrapper onClick={() =>setDisplayInfo(!displayInfo)} displayInfo={displayInfo}>
                <Title>{title || ''}</Title>
                <div>
                    {displayInfo ? 
                        <ArrowUpIcon fill={'#B2B8BE'}/> :
                        <ArrowDownIcon  fill={'#B2B8BE'}/>
                    }
                </div>
            </TitleWrapper>
            <Collapse in={displayInfo}>
                <div>

                    {children}
                </div>
            </Collapse>
        </React.Fragment>
    )
}

export default InfoCard
