import React, {useContext} from 'react';
import Form from 'react-bootstrap/Form';
import InfoCard from './InfoCard';
import { UserDispatchContext } from '../context/user';


const Summary = () => {
    const userDispatch = useContext(UserDispatchContext)
    const onChange = (event) => {
        userDispatch({
            type: 'field',
            fieldName: event.target.id,
            payload: event.currentTarget.value,
        })
    }

    return (
        <Form>
            <InfoCard title={'Sammanfattning av erfarenheter'}>
                <Form.Group controlId="profile">
                    <Form.Text className="text-muted">Beskriv din kompetens och markera dina upplevelser. Beskriv hur du arbetar i projekt och vad dina kollegor s√§ger om dig.</Form.Text>
                    <Form.Control as="textarea" onChange={onChange} rows="3" />
                </Form.Group>
            </InfoCard>
        </Form>
    )
}




export default Summary
