import React, { useContext, useState } from 'react';
import styled from 'styled-components';
import { SkillStateContext, SkillDispatchContext } from 'context/skill'
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import InfoCard from './InfoCard'
import Button from 'react-bootstrap/Button';

const SkillWrapper = styled.div`
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
`

const SkillCard = styled.div`
    background-color: #557A95;
    padding: 5px 10px;
    margin: 5px 0px ;
    color: white;
    border-radius: 10px;
`

const Skill = ({skill}) => (
    <SkillCard>
        <span>{skill}</span>
    </SkillCard>
)

const Skills = () => {
    const state = useContext(SkillStateContext)
    const dipatch = useContext(SkillDispatchContext)
    const [input, setInput] = useState('')
    const onClick = () => {
        dipatch({
            type: 'add',
            payload: input
        })
        setInput('')
    };
    console.log(state)

    return (
        <InfoCard title={'Kompetens'}>
            <InputGroup>
                <Form.Control
                    aria-describedby="basic-addon2"
                    onChange={(event) => setInput(event.target.value)}
                    value={input}
                    />
                <InputGroup.Append>
                    <Button onClick={onClick} variant="outline-secondary">LÃ¤gg till kompetens</Button>
                </InputGroup.Append>
            </InputGroup>
            <SkillWrapper>
                {state.map((value, index)=>
                    <Skill
                        key={value}
                        skill={value}
                    />
                )}
            </SkillWrapper>
        </InfoCard>
    )
}



export default Skills
