import React, {useContext, useEffect} from 'react';
import Form from 'react-bootstrap/Form';
import { UserDispatchContext } from '../../context/user';


const Summary = () => {
    let typingTimer = null;
    const userDispatch = useContext(UserDispatchContext)

    useEffect(() => {
        return function cleanup() {
            clearTimeout(typingTimer);
        };
    });

    const onChange = (event) => {
        const val = event.target.value;
        const id = event.target.id;
        clearTimeout(typingTimer);
        typingTimer = setTimeout(() => {
            if (val) {
                userDispatch({
                    type: 'field',
                    fieldName: id,
                    payload: val,
                })
            }
        }, 3000);
    }

    return (
        <Form.Group controlId="profile">
            <Form.Text className="text-muted">Beskriv din kompetens och markera dina upplevelser. Beskriv hur du arbetar i projekt och vad dina kollegor s√§ger om dig.</Form.Text>
            <Form.Control as="textarea" onChange={onChange} rows="7" />
        </Form.Group>
    )
}




export default Summary
